typeProperties.script:
section Section1;
shared AdventureWorks = let
  AdfDoc = Sql.Database("ncsqldbserver.database.windows.net", "ncsqldb", [CreateNavigationProperties = false]),
  InputTable = AdfDoc{[Schema = "dbo", Item = "Employee"]}[Data]
in
  InputTable;
shared UserQuery = let
  Source = AdventureWorks
in
  Source;
